/**
 * @name arrayZip
 * @description Combines 2 distinct key/value arrays into a single [K, V] array
 */
export function arrayZip(keys, values) {
    const result = new Array(keys.length);
    for (let i = 0; i < keys.length; i++) {
        result[i] = [keys[i], values[i]];
    }
    return result;
}
