/* global BigInt */
import './App.css';
// import { useEffect } from 'react';
import { w_keygen } from "dkg";
import { useWasm } from './useWasm';
import { useState } from 'react';


function App() {
  // make sure the wasm blob is loaded
  useWasm();

  const [threshold, setThreshold] = useState(2);
  // const [numShares, setNumShares] = useState(3);

  return (
    <div className="App">
      <div>
        <h2>DKG Wasm Example</h2>
      </div>
      <div className='body'>
        <span>
          Generate keys
        </span>
        <label htmlFor='threshold-input'>Set threshold</label>
        <input id='threshold-input' type='number' value={threshold} onChange={(e) => setThreshold(e.target.value)} />
        {/* <label htmlFor='shares-input'>Set number of shares</label>
        <input id='shares-input' type='number' value={numShares} onChange={(e) => setNumShares(e.target.value)} /> */}
        <button onClick={() => {
          let k = w_keygen(BigInt(23), threshold);
          console.log(k);
        }}>
          Keygen
        </button>
      </div>
    </div>
  );
}

export default App;
