/* global BigInt */
import './App.css';
import { ApiPromise, WsProvider, Keyring } from "@polkadot/api";
import { 
    w_keygen, w_calculate_secret, 
    w_calculate_pubkey, w_combine_pubkeys,
    w_combine_secrets, w_encrypt, w_threshold_decrypt
} from "dkg-wasm";
import { useWasm } from './useWasm';
import { useEffect, useState } from 'react';

function App() {
  // make sure the wasm blob is loaded
  useWasm();

  const [api, setApi] = useState(null);
  const [acct, setAcct] = useState(null);
  const [societyId, setSocietyId] = useState('');
  const [invitations, setInvitiations] = useState([]);
  const [activeMemberships, setActiveMemberships] = useState([]);

  useEffect(() => {
    const host = "127.0.0.1";
    const port = "9944";
    let provider = new WsProvider(`ws://${host}:${port}`);
    const f = async () => {
      const api = await ApiPromise.create({
        provider,
        types: {
          SocietyId: "Bytes",
        }
      });
      await api.isReady;
      setApi(api);
      // load ALICE account
      const keyring = new Keyring({ type: 'sr25519' });
      let acct = keyring.addFromUri("//Alice");
      setAcct(acct);
      updateMembershipMaps(api, acct);
    }
    f();
  }, []);

  const handleQuerySociety = async(api, id) => {
    let society = await api.query.society.societies(id);
    console.log('society info' + JSON.stringify(society));
    return society;
  }

  const updateMembershipMaps = async (api, acct) => {
    let my_invitations = await api.query.society.membership(acct.address, "invitee");
    let inviteSocs = [];
    my_invitations.forEach(async invite => {
      let society = await handleQuerySociety(api, invite);
      inviteSocs.push({
        id: invite,
        society: society,
      });
    });
    setInvitiations(inviteSocs);

    let activeMemberships = await api.query.society.membership(acct.address, "active");
    let activeSocs = [];
    activeMemberships.forEach(async membership => {
      let society = await handleQuerySociety(api, membership);
      activeSocs.push({
        id: membership,
        society: society,
      });
    });
    setActiveMemberships(activeSocs);
  }

  const Memberships = () => {

    const [isLoading, setIsLoading] = useState(false);
    const [displayText, setDisplayText] = useState(['']);

    useEffect(() => {
        // updateMembershipMaps(api, acct);
    });

    const handleKeygen = (id, threshold, r) => {
      setIsLoading(true);
      setDisplayText([...displayText, 'Generating secrets']);
      // each participant must agree on two numbers
      let r1 = 45432;
      let r2 = 48484;
      let poly = w_keygen(BigInt(r), threshold);
      let secret = w_calculate_secret(poly.coeffs);
      let pubkey = w_calculate_pubkey(BigInt(r1), BigInt(r2), secret);
      
      setDisplayText([...displayText, 'Calculated public key']);
      setDisplayText([...displayText, 'Submitting signed tx']);
      api.tx.society.joinSociety(
        id, pubkey.g2,
      ).signAndSend(acct, result => {
        if (result.isFinalized) {
          console.log('the tx is finalized');
          setDisplayText([...displayText, 'Tx finalized']);
          updateMembershipMaps(api, acct);
          setDisplayText('');
          setIsLoading(false);
        }
      });
    }

    return (
      <div className='membership-container'>
        { isLoading === true ? 
        <div>
          {displayText}
        </div> :
        <div className='container'>
          <span>Invites</span>
          <ul>
          { invitations.map((item, idx) => {
            let threshold = JSON.parse(JSON.stringify(item.society)).threshold;
            return (<li>
              <div className='section'>
                <span>
                  Society Id { item.id }
                </span>
                <span>
                  Founded By { JSON.parse(JSON.stringify(item.society)).founder }
                  {/* { item.society.founder } */}
                </span>
                <span>
                  Threshold { threshold }
                </span>
                <button onClick={() => handleKeygen(item.id, threshold, 23)}>
                  Generate keys (join)
                </button>
              </div>
            </li>);
          }) }
          </ul>
        </div>
        }
        <div className='container'>
          <span>Your Societies</span>
          <ul>
          { activeMemberships.map((s, idx) => {
            return (<li>
              <div className='section'>
                <span> 
                  Name: { JSON.stringify(s.id) }
                </span>
                <button>
                  Calculate Group Public Key
                </button>
              </div>
          </li>);
          }) }
          </ul>
        </div>
      </div>
    );
  }

  const CreateSociety = () => {
    const [threshold, setThreshold] = useState(0);
    const [deadline, setDeadline] = useState(0);
    const [name, setName] = useState('');
    const [id, setId] = useState('');
    // TODO: for now, Alice is the only member
    const [members, setMembers] = useState([]);

    const [isLoading, setIsLoading] = useState(false);

    const handleCreateSociety = () => {
      setIsLoading(true);
      api.tx.society.createSociety(
        id, threshold, name, deadline, [acct.address]
      ).signAndSend(acct, result => {
        if (result.isInBlock) {
          console.log('the tx was successful');
        } 
        if (result.isFinalized) {
          console.log('the tx is finalized');
          // will emit an event in the future
          setIsLoading(false);
          updateMembershipMaps(api, acct);
        }
      });
    }

    return (
      <div className='section'>
        <span>
          Create a Society
        </span>
        {isLoading === true ? 
        <div>
          <p>Loading...</p>
        </div> :
        <div className='form'> 

        <label htmlFor='threshold'>threshold</label>
        <input id="threshold" type="number" value={threshold} onChange={e => setThreshold(e.target.value)} />

        <label htmlFor='id'>id</label>
        <input id="id" type="text" value = {id} onChange={e => setId(e.target.value)} />

        <label htmlFor='name'>name</label>
        <input id="name" type="text" value = {name} onChange={e => setName(e.target.value)} />

        <label htmlFor='deadline'>deadline</label>
        <input id="deadline" type="number" value = {deadline} onChange={e => setDeadline(e.target.value)} />

        <button onClick={handleCreateSociety}> Create Society
        </button>
        </div>
        }
      </div>
    );
  }

  // const calculateGroupPublicKey = () => {
  //   return society.reduce((a, b) => w_combine_pubkeys(a.pubkey, b.pubkey));
  // }

  // const calculateGroupSecretKey = () => {
  //   return society.reduce((a, b) => w_combine_secrets(a.secret, b.secret));
  // }

  return (
    <div className="App">
      <div className='header'>
        <h2>Cryptex</h2>
        <span>
          Api is { api === null ? 'not' : '' } ready
        </span>
      </div>
      <div className='body'>
        <div className='section'>
          <Memberships />
        </div>
        <div className='section'>
          <CreateSociety />
        </div>
      </div>
    </div>
  );
}

export default App;
